import NodeMaterial from 'three/src/materials/nodes/NodeMaterial.js';
import { materialReference } from 'three/src/nodes/accessors/MaterialReferenceNode.js';
import { diffuseColor } from 'three/src/nodes/core/PropertyNode.js';
import { vec3 } from 'three/src/nodes/tsl/TSLBase.js';
import { mix } from 'three/src/nodes/math/MathNode.js';
import { matcapUV } from 'three/src/nodes/utils/MatcapUVNode.js';

import { MeshMatcapMaterial } from 'three/src/materials/MeshMatcapMaterial.js';

const _defaultValues = /*@__PURE__*/ new MeshMatcapMaterial();

class MeshMatcapNodeMaterial extends NodeMaterial {

	static get type() {

		return 'MeshMatcapNodeMaterial';

	}

	constructor( parameters ) {

		super();

		this.lights = false;

		this.isMeshMatcapNodeMaterial = true;

		this.setDefaultValues( _defaultValues );

		this.setValues( parameters );

	}

	setupVariants( builder ) {

		const uv = matcapUV;

		let matcapColor;

		if ( builder.material.matcap ) {

			matcapColor = materialReference( 'matcap', 'texture' ).context( { getUV: () => uv } );

		} else {

			matcapColor = vec3( mix( 0.2, 0.8, uv.y ) ); // default if matcap is missing

		}

		diffuseColor.rgb.mulAssign( matcapColor.rgb );

	}

}


export default MeshMatcapNodeMaterial;
