import NodeMaterial from 'three/src/materials/nodes/NodeMaterial.js';
import { diffuseColor } from 'three/src/nodes/core/PropertyNode.js';
import { directionToColor } from 'three/src/nodes/utils/Packing.js';
import { materialOpacity } from 'three/src/nodes/accessors/MaterialNode.js';
import { transformedNormalView } from 'three/src/nodes/accessors/Normal.js';
import { float, vec4 } from 'three/src/nodes/tsl/TSLBase.js';

import { MeshNormalMaterial } from 'three/src/materials/MeshNormalMaterial.js';

const _defaultValues = /*@__PURE__*/ new MeshNormalMaterial();

class MeshNormalNodeMaterial extends NodeMaterial {

	static get type() {

		return 'MeshNormalNodeMaterial';

	}

	constructor( parameters ) {

		super();

		this.lights = false;

		this.isMeshNormalNodeMaterial = true;

		this.setDefaultValues( _defaultValues );

		this.setValues( parameters );

	}

	setupDiffuseColor() {

		const opacityNode = this.opacityNode ? float( this.opacityNode ) : materialOpacity;

		diffuseColor.assign( vec4( directionToColor( transformedNormalView ), opacityNode ) );

	}

}

export default MeshNormalNodeMaterial;
