import ViewportTextureNode from 'three/src/nodes/display/ViewportTextureNode.js';
import { nodeProxy } from 'three/src/nodes/tsl/TSLBase.js';
import { screenUV } from 'three/src/nodes/display/ScreenNode.js';

import { FramebufferTexture } from 'three/src/textures/FramebufferTexture.js';

let _sharedFramebuffer = null;

class ViewportSharedTextureNode extends ViewportTextureNode {

	static get type() {

		return 'ViewportSharedTextureNode';

	}

	constructor( uvNode = screenUV, levelNode = null ) {

		if ( _sharedFramebuffer === null ) {

			_sharedFramebuffer = new FramebufferTexture();

		}

		super( uvNode, levelNode, _sharedFramebuffer );

	}

	updateReference() {

		return this;

	}

}

export default ViewportSharedTextureNode;

export const viewportSharedTexture = /*@__PURE__*/ nodeProxy( ViewportSharedTextureNode );
