import Node from 'three/src/nodes/core/Node.js';
import { positionView } from 'three/src/nodes/accessors/Position.js';
import { nodeProxy } from 'three/src/nodes/tsl/TSLBase.js';

class FogNode extends Node {

	static get type() {

		return 'FogNode';

	}

	constructor( colorNode, factorNode ) {

		super( 'float' );

		this.isFogNode = true;

		this.colorNode = colorNode;
		this.factorNode = factorNode;

	}

	getViewZNode( builder ) {

		let viewZ;

		const getViewZ = builder.context.getViewZ;

		if ( getViewZ !== undefined ) {

			viewZ = getViewZ( this );

		}

		return ( viewZ || positionView.z ).negate();

	}

	setup() {

		return this.factorNode;

	}

}

export default FogNode;

export const fog = /*@__PURE__*/ nodeProxy( FogNode );
