import FogNode from 'three/src/nodes/fog/FogNode.js';
import { smoothstep } from 'three/src/nodes/math/MathNode.js';
import { nodeProxy } from 'three/src/nodes/tsl/TSLBase.js';

class FogRangeNode extends FogNode {

	static get type() {

		return 'FogRangeNode';

	}

	constructor( colorNode, nearNode, farNode ) {

		super( colorNode );

		this.isFogRangeNode = true;

		this.nearNode = nearNode;
		this.farNode = farNode;

	}

	setup( builder ) {

		const viewZ = this.getViewZNode( builder );

		return smoothstep( this.nearNode, this.farNode, viewZ );

	}

}

export default FogRangeNode;

export const rangeFog = /*@__PURE__*/ nodeProxy( FogRangeNode );
